// page init
jQuery(function(){
	initTabs();
	initCustomForms();
	initDatepicker();
	initOpenClose();
	initAccordion();
	initLightbox();
	initMobileNav();
	initFormValidation();
});

// initialize custom form elements
function initCustomForms() {
	jcf.setOptions('Select', {
		wrapNative: false,
		wrapNativeOnMobile: false,
		maxVisibleItems: 6
	});
	jcf.replaceAll();
}

// content tabs init
function initTabs() {
	jQuery('ul.tabset').tabset({
		tabLinks: 'a',
		activeClass: 'tab-active',
		addToParent: true,
		defaultTab: false
	});
}

// open-close init
function initOpenClose() {
	jQuery('#nav li').openClose({
		hideOnClickOutside: true,
		activeClass: 'drop-active',
		opener: '.opener',
		slider: '.dropdown',
		animSpeed: 400,
		effect: 'slide'
	});
	jQuery('.menu').openClose({
		hideOnClickOutside: true,
		activeClass: 'active',
		opener: '.opener, a.img-opener',
		slider: '.dropdown',
		animSpeed: 400,
		effect: 'slide'
	});
}

// accordion menu init
function initAccordion() {
	jQuery('.accordion').slideAccordion({
		opener: 'a.opener',
		slider: '.slide',
		animSpeed: 300
	});
}

// fancybox modal popup init
function initLightbox() {
	jQuery('a.lightbox, a[rel*="lightbox"]').fancybox({
		helpers: {
			overlay: {
				css: {
					background: 'rgba(39, 53, 66, 0.30)'
				}
			}
		},
		afterLoad: function(current, previous) {
			// handle custom close button in inline modal
			if(current.href.indexOf('#') === 0) {
				jQuery(current.href).find('a.close').off('click.fb').on('click.fb', function(e){
					e.preventDefault();
					jQuery.fancybox.close();
				});
			}
		},
		padding: 0
	});
}

// mobile menu init
function initMobileNav() {
	jQuery('body').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'nav-active',
		menuOpener: '.nav-opener',
		menuDrop: '#nav'
	});
	jQuery('body').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'search-active',
		menuOpener: '.search-opener',
		menuDrop: '.search-form'
	});
	jQuery('body').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'sidebar-active',
		menuOpener: '.sidebar-opener',
		menuDrop: '#sidebar'
	});
	jQuery('body').mobileNav({
		menuActiveClass: 'img-opened',
		menuOpener: '.img-opener, .btn-img-close'
	});
	jQuery('body').mobileNav({
		menuActiveClass: 'fullscreen-active',
		menuOpener: '.btn-fullscreen'
	});
	jQuery('body').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'img-menu-active',
		menuOpener: '.menu-opener',
		menuDrop: '.img-editor .nav'
	});
}

function initFormValidation() {
	jQuery('.form-validation').formValidation({
		errorClass: 'input-error'
	});
}

function initDatepicker() {
	jQuery('[data-datepicker]').uiDatepicker();
}

